* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  min-height: 100vh;
  color: #fff;
  padding: 20px;
}

h1 {
  text-align: center;
  margin-bottom: 10px;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.info-bar {
  text-align: center;
  padding: 10px 20px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  margin-bottom: 20px;
  display: flex;
  justify-content: center;
  gap: 30px;
  flex-wrap: wrap;
}

.info-bar span {
  font-size: 0.9rem;
  color: #a0a0a0;
}

.info-bar strong {
  color: #4fc3f7;
}

#simulator-container {
  max-width: 1200px;
  margin: 0 auto;
}

.building {
  display: flex;
  gap: 30px;
  justify-content: center;
  align-items: flex-start;
}

.elevator-shaft-area {
  position: relative;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  padding: 20px;
  padding-left: 120px;
}

.floor {
  display: flex;
  align-items: center;
  height: 60px;
  position: relative;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.floor:last-child {
  border-bottom: none;
}

.floor-number {
  position: absolute;
  left: -110px;
  width: 70px;
  text-align: right;
  font-weight: 600;
  color: #b0b0b0;
  font-size: 0.85rem;
}

.floor-buttons {
  position: absolute;
  left: -35px;
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.call-btn {
  width: 28px;
  height: 24px;
  border: none;
  border-radius: 4px;
  background: #2d3748;
  color: #718096;
  cursor: pointer;
  font-size: 10px;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.call-btn:hover {
  background: #4a5568;
  transform: scale(1.1);
}

.call-btn.active {
  background: #f6ad55;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(246, 173, 85, 0.5);
}

.up-btn.active {
  background: #48bb78;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(72, 187, 120, 0.5);
}

.down-btn.active {
  background: #fc8181;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(252, 129, 129, 0.5);
}

.floor-line {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  height: 2px;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
}

.elevator-shafts {
  display: flex;
  gap: 15px;
  margin-left: 20px;
}

.elevator-shaft {
  width: 60px;
  height: 600px; /* 10 floors * 60px */
  background: linear-gradient(180deg, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.1) 100%);
  border-radius: 8px;
  position: relative;
  border: 2px solid rgba(255, 255, 255, 0.1);
  overflow: hidden;
}

.elevator-shaft::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: repeating-linear-gradient(
    180deg,
    rgba(255, 255, 255, 0.1) 0px,
    rgba(255, 255, 255, 0.1) 5px,
    transparent 5px,
    transparent 10px
  );
  transform: translateX(-50%);
}

.elevator {
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 50px;
  height: 50px;
  background: linear-gradient(180deg, #667eea 0%, #764ba2 100%);
  border-radius: 6px;
  transition: bottom 0.8s ease-in-out;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

.elevator-number {
  font-weight: bold;
  font-size: 14px;
  color: #fff;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
}

.elevator-doors {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 30px;
