{"content": "/* Elevator Simulator Styles */

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  min-height: 100vh;
  color: #fff;
  padding: 20px;
}

h1 {
  text-align: center;
  margin-bottom: 10px;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.info-bar {
  text-align: center;
  padding: 10px 20px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  margin-bottom: 20px;
  display: flex;
  justify-content: center;
  gap: 30px;
  flex-wrap: wrap;
}

.info-bar span {
  font-size: 0.9rem;
  color: #a0a0a0;
}

.info-bar strong {
  color: #4fc3f7;
}

#simulator-container {
  max-width: 1200px;
  margin: 0 auto;
}

.building {
  display: flex;
  gap: 30px;
  justify-content: center;
  align-items: flex-start;
}

.elevator-shaft-area {
  position: relative;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  padding: 20px;
  padding-left: 120px;
}

.floor {
  display: flex;
  align-items: center;
  height: 60px;
  position: relative;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.floor:last-child {
  border-bottom: none;
}

.floor-number {
  position: absolute;
  left: -110px;
  width: 70px;
  text-align: right;
  font-weight: 600;
  color: #b0b0b0;
  font-size: 0.85rem;
}

.floor-buttons {
  position: absolute;
  left: -35px;
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.call-btn {
  width: 28px;
  height: 24px;
  border: none;
  border-radius: 4px;
  background: #2d3748;
  color: #718096;
  cursor: pointer;
  font-size: 10px;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.call-btn:hover {
  background: #4a5568;
  transform: scale(1.1);
}

.call-btn.active {
  background: #f6ad55;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(246, 173, 85, 0.5);
}

.up-btn.active {
  background: #48bb78;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(72, 187, 120, 0.5);
}

.down-btn.active {
  background: #fc8181;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(252, 129, 129, 0.5);
}

.floor-line {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  height: 2px;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
}

.elevator-shafts {
  display: flex;
  gap: 15px;
  margin-left: 20px;
}

.elevator-shaft {
  width: 60px;
  height: 600px; /* 10 floors * 60px */
  background: linear-gradient(180deg, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.1) 100%);
  border-radius: 8px;
  position: relative;
  border: 2px solid rgba(255, 255, 255, 0.1);
  overflow: hidden;
}

.elevator-shaft::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: repeating-linear-gradient(
    180deg,
    rgba(255, 255, 255, 0.1) 0px,
    rgba(255, 255, 255, 0.1) 5px,
    transparent 5px,
    transparent 10px
  );
  transform: translateX(-50%);
}

.elevator {
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 50px;
  height: 50px;
  background: linear-gradient(180deg, #667eea 0%, #764ba2 100%);
  border-radius: 6px;
  transition: bottom 0.8s ease-in-out;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

.elevator-number {
  font-weight: bold;
  font-size: 14px;
  color: #fff;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
}

.elevator-doors {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 30px;
  height: 35px;
  background: linear-gradient(90deg, 
    rgba(255, 255, 255, 0.3) 0%, 
    rgba(255, 255, 255, 0.1) 49%, 
    rgba(0, 0, 0, 0.2) 50%, 
    rgba(255, 255, 255, 0.1) 51%, 
    rgba(255, 255, 255, 0.3) 100%
  );
  border-radius: 3px;
  transition: all 0.3s ease;
}

.elevator.doors-open .elevator-doors {
  width: 40px;
  background: linear-gradient(90deg, 
    rgba(255, 255, 255, 0.6) 0%, 
    rgba(255, 255, 255, 0.2) 20%, 
    rgba(0, 0, 0, 0.4) 50%, 
    rgba(255, 255, 255, 0.2) 80%, 
    rgba(255, 255, 255, 0.6) 100%
  );
  box-shadow: inset 0 0 10px rgba(255, 215, 0, 0.5);
}

.elevator.doors-open {
  box-shadow: 0 4px 20px rgba(255, 215, 0, 0.6);
}

.elevator-panels {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.elevator-panel {
  background: linear-gradient(135deg, #2d3748 0%, #1a202c 100%);
  border-radius: 12px;
  padding: 15px;
  min-width: 120px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.panel-title {
  text-align: center;
  font-weight: 600;
  margin-bottom: 12px;
  color: #a0aec0;
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.panel-buttons {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 5px;
}

.floor-btn {
  width: 32px;
  height: 32px;
  border: none;
  border-radius: 6px;
  background: #4a5568;
  color: #e2e8f0;
  cursor: pointer;
  font-size: 12px;
  font-weight: 600;
  transition: all 0.2s ease;
}

.floor-btn:hover {
  background: #667eea;
  transform: scale(1.1);
}

.floor-btn.active {
  background: #f6ad55;
  color: #1a202c;
  box-shadow: 0 0 10px rgba(246, 173, 85, 0.5);
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0%, 100% {
    box-shadow: 0 0 10px rgba(246, 173, 85, 0.5);
  }
  50% {
    box-shadow: 0 0 20px rgba(246, 173, 85, 0.8);
  }
}

/* Responsive adjustments */
@media (max-width: 900px) {
  .building {
    flex-direction: column;
    align-items: center;
  }

  .elevator-panels {
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
  }
}

/* Instructions */
.instructions {
  max-width: 800px;
  margin: 30px auto 0;
  padding: 20px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  font-size: 0.9rem;
  color: #a0a0a0;
}

.instructions h3 {
  color: #4fc3f7;
  margin-bottom: 10px;
}

.instructions ul {
  margin-left: 20px;
}

.instructions li {
  margin-bottom: 5px;
}
