{"filePath": "/Users/championswimmer/Development/Personal/LLM/elevator-bench/outputs/copilot_claude-sonnet-45/Elevator.ts", "content": "export enum Direction {
  IDLE = 'IDLE',
  UP = 'UP',
  DOWN = 'DOWN'
}

export enum ElevatorState {
  IDLE = 'IDLE',
  MOVING = 'MOVING',
  STOPPED = 'STOPPED'
}

export class Elevator {
  id: number;
  currentFloor: number;
  direction: Direction;
  state: ElevatorState;
  destinationFloors: Set<number>;
  requestedFloors: Map<number, Direction>; // floor -> direction requested from that floor

  constructor(id: number, startFloor: number = 0) {
    this.id = id;
    this.currentFloor = startFloor;
    this.direction = Direction.IDLE;
    this.state = ElevatorState.IDLE;
    this.destinationFloors = new Set();
    this.requestedFloors = new Map();
  }

  addDestination(floor: number): void {
    if (floor !== this.currentFloor) {
      this.destinationFloors.add(floor);
    }
  }

  addRequest(floor: number, direction: Direction): void {
    if (floor !== this.currentFloor) {
      this.requestedFloors.set(floor, direction);
      this.destinationFloors.add(floor);
    }
  }

  hasDestinations(): boolean {
    return this.destinationFloors.size > 0;
  }

  getNextFloor(): number | null {
    if (!this.hasDestinations()) {
      return null;
    }

    const destinations = Array.from(this.destinationFloors).sort((a, b) => a - b);

    if (this.direction === Direction.UP) {
      // Find floors above current floor
      const floorsAbove = destinations.filter(f => f > this.currentFloor);
      if (floorsAbove.length > 0) {
        return floorsAbove[0]; // Closest floor above
      }
      // No floors above, switch direction to DOWN
      const floorsBelow = destinations.filter(f => f < this.currentFloor);
      if (floorsBelow.length > 0) {
        return floorsBelow[floorsBelow.length - 1]; // Highest floor below
      }
    } else if (this.direction === Direction.DOWN) {
      // Find floors below current floor
      const floorsBelow = destinations.filter(f => f < this.currentFloor);
      if (floorsBelow.length > 0) {
        return floorsBelow[floorsBelow.length - 1]; // Closest floor below
      }
      // No floors below, switch direction to UP
      const floorsAbove = destinations.filter(f => f > this.currentFloor);
      if (floorsAbove.length > 0) {
        return floorsAbove[0]; // Lowest floor above
      }
    } else {
      // IDLE - go to closest floor
      let closestFloor = destinations[0];
      let minDistance = Math.abs(destinations[0] - this.currentFloor);

      for (const floor of destinations) {
        const distance = Math.abs(floor - this.currentFloor);
        if (distance < minDistance) {
          minDistance = distance;
          closestFloor = floor;
        }
      }
      return closestFloor;
    }

    return null;
  }

  moveToNextFloor(): void {
    const nextFloor = this.getNextFloor();

    if (nextFloor === null) {
      this.direction = Direction.IDLE;
      this.state = ElevatorState.IDLE;
      return;
    }

    // Determine direction
    if (nextFloor > this.currentFloor) {
      this.direction = Direction.UP;
      this.currentFloor++;
    } else if (nextFloor < this.currentFloor) {
      this.direction = Direction.DOWN;
      this.currentFloor--;
    }

    this.state = ElevatorState.MOVING;

    // Check if we've reached a destination
    if (this.destinationFloors.has(this.currentFloor)) {
      this.state = ElevatorState.STOPPED;
      this.destinationFloors.delete(this.currentFloor);
      this.requestedFloors.delete(this.currentFloor);

      // If no more destinations, become idle
      if (!this.hasDestinations()) {
        this.direction = Direction.IDLE;
        this.state = ElevatorState.IDLE;
      }
    }
  }

  shouldStopAtFloor(floor: number): boolean {
    return this.destinationFloors.has(floor);
  }

  getDistanceToFloor(floor: number): number {
    return Math.abs(this.currentFloor - floor);
  }

  isIdle(): boolean {
    return this.state === ElevatorState.IDLE && this.direction === Direction.IDLE;
  }

  isMovingTowards(floor: number): boolean {
    if (this.isIdle()) {
      return true; // Idle elevators can go anywhere
    }

    if (this.direction === Direction.UP && floor > this.currentFloor) {
      return true;
    }

    if (this.direction === Direction.DOWN && floor < this.currentFloor) {
      return true;
    }

    return false;
  }
}
"}

