{"content": "import { Building, DEFAULT_CONFIG } from './Building';
import { Direction } from './Elevator';

// Create building instance
const building = new Building(DEFAULT_CONFIG);

// UI Update function
function updateUI(): void {
  const state = building.getState();

  // Update floor buttons
  state.floors.forEach((floor) => {
    const upButton = document.querySelector(`[data-floor="${floor.floorNumber}"][data-direction="up"]`);
    const downButton = document.querySelector(`[data-floor="${floor.floorNumber}"][data-direction="down"]`);

    if (upButton) {
      upButton.classList.toggle('active', floor.upButtonPressed);
    }
    if (downButton) {
      downButton.classList.toggle('active', floor.downButtonPressed);
    }
  });

  // Update elevators
  state.elevators.forEach((elevator) => {
    const elevatorEl = document.querySelector(`[data-elevator-id="${elevator.id}"]`);
    if (elevatorEl) {
      // Calculate position (bottom = 0, top = max floors)
      const totalFloors = building.config.numFloors;
      const positionPercent = (elevator.currentFloor / (totalFloors - 1)) * 100;

      (elevatorEl as HTMLElement).style.bottom = `${positionPercent}%`;

      // Update direction indicator
      const directionEl = elevatorEl.querySelector('.elevator-direction');
      if (directionEl) {
        directionEl.textContent = getDirectionSymbol(elevator.direction);
      }

      // Update floor display
      const floorDisplay = elevatorEl.querySelector('.elevator-floor');
      if (floorDisplay) {
        floorDisplay.textContent = elevator.currentFloor.toString();
      }

      // Update state class
      elevatorEl.classList.remove('idle', 'moving', 'stopped');
      elevatorEl.classList.add(elevator.state.toLowerCase());

      // Update destination indicators
      updateElevatorPanel(elevator.id, elevator.destinations);
    }
  });
}

function getDirectionSymbol(direction: Direction): string {
  switch (direction) {
    case Direction.UP:
      return '';
    case Direction.DOWN:
      return '';
    case Direction.IDLE:
      return '';
    default:
      return '';
  }
}

function updateElevatorPanel(elevatorId: number, destinations: number[]): void {
  const panel = document.querySelector(`[data-panel-id="${elevatorId}"]`);
  if (panel) {
    const buttons = panel.querySelectorAll('.floor-button');
    buttons.forEach((button) => {
      const floor = parseInt((button as HTMLElement).dataset.floor || '0');
      button.classList.toggle('selected', destinations.includes(floor));
    });
  }
}

// Initialize UI
function initializeUI(): void {
  const building_wrapper = document.getElementById('building');
  const controlsWrapper = document.getElementById('controls');

  if (!building_wrapper || !controlsWrapper) {
    console.error('Required DOM elements not found');
    return;
  }

  // Create floor controls
  const floorControlsDiv = document.createElement('div');
  floorControlsDiv.className = 'floor-controls';

  for (let i = building.config.numFloors - 1; i >= 0; i--) {
    const floorDiv = document.createElement('div');
    floorDiv.className = 'floor';
    floorDiv.dataset.floor = i.toString();

    const floorLabel = document.createElement('div');
    floorLabel.className = 'floor-label';
    floorLabel.textContent = `Floor ${i}`;

    const floorButtons = document.createElement('div');
    floorButtons.className = 'floor-buttons';

    // Up button (not for top floor)
    if (i < building.config.numFloors - 1) {
      const upButton = document.createElement('button');
      upButton.className = 'call-button';
      upButton.dataset.floor = i.toString();
      upButton.dataset.direction = 'up';
      upButton.textContent = '';
      upButton.title = 'Call elevator going up';
      upButton.addEventListener('click', () => {
        building.requestElevator(i, Direction.UP);
        updateUI();
      });
      floorButtons.appendChild(upButton);
    }

    // Down button (not for ground floor)
    if (i > 0) {
      const downButton = document.createElement('button');
      downButton.className = 'call-button';
      downButton.dataset.floor = i.toString();
      downButton.dataset.direction = 'down';
      downButton.textContent = '';
      downButton.title = 'Call elevator going down';
      downButton.addEventListener('click', () => {
        building.requestElevator(i, Direction.DOWN);
        updateUI();
      });
      floorButtons.appendChild(downButton);
    }

    floorDiv.appendChild(floorLabel);
    floorDiv.appendChild(floorButtons);
    floorControlsDiv.appendChild(floorDiv);
  }

  building_wrapper.appendChild(floorControlsDiv);

  // Create elevator shafts
  const elevatorsDiv = document.createElement('div');
  elevatorsDiv.className = 'elevators';

  for (let i = 0; i < building.config.numElevators; i++) {
    const shaftDiv = document.createElement('div');
    shaftDiv.className = 'elevator-shaft';

    const elevatorDiv = document.createElement('div');
    elevatorDiv.className = 'elevator idle';
    elevatorDiv.dataset.elevatorId = i.toString();

    const elevatorLabel = document.createElement('div');
    elevatorLabel.className = 'elevator-label';
    elevatorLabel.textContent = `E${i}`;

    const elevatorInfo = document.createElement('div');
    elevatorInfo.className = 'elevator-info';

    const floorDisplay = document.createElement('div');
    floorDisplay.className = 'elevator-floor';
    floorDisplay.textContent = '0';

    const directionDisplay = document.createElement('div');
    directionDisplay.className = 'elevator-direction';
    directionDisplay.textContent = '';

    elevatorInfo.appendChild(floorDisplay);
    elevatorInfo.appendChild(directionDisplay);

    elevatorDiv.appendChild(elevatorLabel);
    elevatorDiv.appendChild(elevatorInfo);

    shaftDiv.appendChild(elevatorDiv);
    elevatorsDiv.appendChild(shaftDiv);
  }

  building_wrapper.appendChild(elevatorsDiv);

  // Create elevator control panels
  const panelsDiv = document.createElement('div');
  panelsDiv.className = 'elevator-panels';

  for (let i = 0; i < building.config.numElevators; i++) {
    const panelDiv = document.createElement('div');
    panelDiv.className = 'elevator-panel';
    panelDiv.dataset.panelId = i.toString();

    const panelTitle = document.createElement('div');
    panelTitle.className = 'panel-title';
    panelTitle.textContent = `Elevator ${i}`;
    panelDiv.appendChild(panelTitle);

    const buttonsGrid = document.createElement('div');
    buttonsGrid.className = 'panel-buttons';

    for (let floor = building.config.numFloors - 1; floor >= 0; floor--) {
      const floorButton = document.createElement('button');
      floorButton.className = 'floor-button';
      floorButton.dataset.floor = floor.toString();
      floorButton.textContent = floor.toString();
      floorButton.addEventListener('click', () => {
        building.selectFloor(i, floor);
        updateUI();
      });
      buttonsGrid.appendChild(floorButton);
    }

    panelDiv.appendChild(buttonsGrid);
    panelsDiv.appendChild(panelDiv);
  }

  controlsWrapper.appendChild(panelsDiv);

  // Create control buttons
  const controlButtonsDiv = document.createElement('div');
  controlButtonsDiv.className = 'control-buttons';

  const startButton = document.createElement('button');
  startButton.id = 'start-button';
  startButton.className = 'control-btn';
  startButton.textContent = 'Start Simulation';
  startButton.addEventListener('click', () => {
    building.start(updateUI);
    startButton.disabled = true;
    stopButton.disabled = false;
    resetButton.disabled = false;
  });

  const stopButton = document.createElement('button');
  stopButton.id = 'stop-button';
  stopButton.className = 'control-btn';
  stopButton.textContent = 'Stop Simulation';
  stopButton.disabled = true;
  stopButton.addEventListener('click', () => {
    building.stop();
    startButton.disabled = false;
    stopButton.disabled = true;
  });

  const resetButton = document.createElement('button');
  resetButton.id = 'reset-button';
  resetButton.className = 'control-btn';
  resetButton.textContent = 'Reset';
  resetButton.disabled = true;
  resetButton.addEventListener('click', () => {
    building.reset();
    updateUI();
    startButton.disabled = false;
    stopButton.disabled = true;
    resetButton.disabled = true;
  });

  controlButtonsDiv.appendChild(startButton);
  controlButtonsDiv.appendChild(stopButton);
  controlButtonsDiv.appendChild(resetButton);

  controlsWrapper.appendChild(controlButtonsDiv);

  // Initial UI update
  updateUI();
}

// Initialize when DOM is loaded
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', initializeUI);
} else {
  initializeUI();
}
