{"content": "import { Elevator, Direction, ElevatorState } from './Elevator';
import { Floor } from './Floor';

export interface BuildingConfig {
  numFloors: number;
  numElevators: number;
  floorTravelTime: number; // ms per floor
}

export const DEFAULT_CONFIG: BuildingConfig = {
  numFloors: 10,
  numElevators: 4,
  floorTravelTime: 1000 // 1 second per floor
};

export class Building {
  config: BuildingConfig;
  elevators: Elevator[];
  floors: Floor[];
  requestQueue: Array<{ floor: number; direction: Direction }>;
  private intervalId: number | null = null;

  constructor(config: BuildingConfig = DEFAULT_CONFIG) {
    this.config = config;
    this.elevators = [];
    this.floors = [];
    this.requestQueue = [];

    // Initialize elevators
    for (let i = 0; i < config.numElevators; i++) {
      this.elevators.push(new Elevator(i, 0));
    }

    // Initialize floors
    for (let i = 0; i < config.numFloors; i++) {
      this.floors.push(new Floor(i));
    }
  }

  requestElevator(floor: number, direction: Direction): void {
    const floorObj = this.floors[floor];

    if (direction === Direction.UP) {
      floorObj.pressUpButton();
    } else if (direction === Direction.DOWN) {
      floorObj.pressDownButton();
    }

    // Try to assign an elevator immediately
    const elevator = this.findBestElevator(floor, direction);

    if (elevator) {
      elevator.addRequest(floor, direction);
    } else {
      // Queue the request if no elevator is suitable
      this.requestQueue.push({ floor, direction });
    }
  }

  selectFloor(elevatorId: number, floor: number): void {
    const elevator = this.elevators[elevatorId];
    if (elevator) {
      elevator.addDestination(floor);
    }
  }

  findBestElevator(floor: number, direction: Direction): Elevator | null {
    let bestElevator: Elevator | null = null;
    let minDistance = Infinity;

    // First, try to find an idle elevator
    for (const elevator of this.elevators) {
      if (elevator.isIdle()) {
        const distance = elevator.getDistanceToFloor(floor);
        if (distance < minDistance) {
          minDistance = distance;
          bestElevator = elevator;
        }
      }
    }

    if (bestElevator) {
      return bestElevator;
    }

    // If no idle elevator, find one moving in the same direction towards the floor
    for (const elevator of this.elevators) {
      if (elevator.direction === direction && elevator.isMovingTowards(floor)) {
        const distance = elevator.getDistanceToFloor(floor);
        if (distance < minDistance && 
            ((direction === Direction.UP && elevator.currentFloor <= floor) ||
             (direction === Direction.DOWN && elevator.currentFloor >= floor))) {
          minDistance = distance;
          bestElevator = elevator;
        }
      }
    }

    return bestElevator;
  }

  processRequestQueue(): void {
    const remainingRequests: Array<{ floor: number; direction: Direction }> = [];

    for (const request of this.requestQueue) {
      const elevator = this.findBestElevator(request.floor, request.direction);

      if (elevator) {
        elevator.addRequest(request.floor, request.direction);
      } else {
        remainingRequests.push(request);
      }
    }

    this.requestQueue = remainingRequests;
  }

  update(): void {
    // Move all elevators
    for (const elevator of this.elevators) {
      if (elevator.hasDestinations()) {
        const previousFloor = elevator.currentFloor;
        elevator.moveToNextFloor();

        // Clear floor buttons if elevator stopped at the floor
        if (elevator.state === ElevatorState.STOPPED) {
          const floor = this.floors[elevator.currentFloor];

          // Clear the button for the direction the elevator is traveling
          if (elevator.direction === Direction.UP || 
              (elevator.direction === Direction.IDLE && previousFloor < elevator.currentFloor)) {
            floor.clearUpButton();
          } else if (elevator.direction === Direction.DOWN || 
                     (elevator.direction === Direction.IDLE && previousFloor > elevator.currentFloor)) {
            floor.clearDownButton();
          }
        }
      }
    }

    // Process queued requests
    this.processRequestQueue();
  }

  start(onUpdate?: () => void): void {
    if (this.intervalId !== null) {
      return; // Already running
    }

    this.intervalId = window.setInterval(() => {
      this.update();
      if (onUpdate) {
        onUpdate();
      }
    }, this.config.floorTravelTime);
  }

  stop(): void {
    if (this.intervalId !== null) {
      clearInterval(this.intervalId);
      this.intervalId = null;
    }
  }

  reset(): void {
    this.stop();

    // Reset all elevators
    for (let i = 0; i < this.config.numElevators; i++) {
      this.elevators[i] = new Elevator(i, 0);
    }

    // Reset all floors
    for (let i = 0; i < this.config.numFloors; i++) {
      this.floors[i] = new Floor(i);
    }

    this.requestQueue = [];
  }

  getState() {
    return {
      elevators: this.elevators.map(e => ({
        id: e.id,
        currentFloor: e.currentFloor,
        direction: e.direction,
        state: e.state,
        destinations: Array.from(e.destinationFloors)
      })),
      floors: this.floors.map(f => ({
        floorNumber: f.floorNumber,
        upButtonPressed: f.upButtonPressed,
        downButtonPressed: f.downButtonPressed
      })),
      queueLength: this.requestQueue.length
    };
  }
}
