  upButtonPressed: boolean;
  downButtonPressed: boolean;

  constructor(floorNumber: number) {
    this.floorNumber = floorNumber;
    this.upButtonPressed = false;
    this.downButtonPressed = false;
  }

  pressUpButton(): void {
    this.upButtonPressed = true;
  }

  pressDownButton(): void {
    this.downButtonPressed = true;
  }

  clearUpButton(): void {
    this.upButtonPressed = false;
  }

  clearDownButton(): void {
    this.downButtonPressed = false;
  }

  clearButton(direction: Direction): void {
    if (direction === Direction.UP) {
      this.clearUpButton();
    } else if (direction === Direction.DOWN) {
      this.clearDownButton();
    }
  }

  hasRequest(): boolean {
    return this.upButtonPressed || this.downButtonPressed;
  }
}
