  margin-bottom: 5px;
}

.info-label {
  font-weight: bold;
  color: #a0aec0;
}

.info-value {
  color: #e2e8f0;
}

main {
  display: flex;
  padding: 30px;
  gap: 30px;
  min-height: 800px;
}

.simulation-area {
  flex: 2;
  display: flex;
  gap: 20px;
  background: #f7fafc;
  border-radius: 10px;
  padding: 20px;
  position: relative;
}

#floors-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-width: 120px;
}

.floor {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  padding: 10px;
