{"filePath": "/Users/championswimmer/Development/Personal/LLM/elevator-bench/outputs/copilot_claude-sonnet-45/Building.ts", "content": "import { Elevator, ElevatorDirection, ElevatorState } from './Elevator.js';
import { Floor, RequestDirection } from './Floor.js';

export interface ElevatorRequest {
  floor: number;
  direction: RequestDirection;
}

export interface Config {
  totalFloors: number;
  totalElevators: number;
  floorTravelTime: number; // milliseconds per floor
  doorOpenTime: number; // milliseconds doors stay open
}

export class Building {
  config: Config;
  floors: Floor[];
  elevators: Elevator[];
  requestQueue: ElevatorRequest[];
  private animationIntervalId: number | null = null;

  constructor(config: Config) {
    this.config = config;
    this.floors = [];
    this.elevators = [];
    this.requestQueue = [];

    // Initialize floors
    for (let i = 0; i < config.totalFloors; i++) {
      this.floors.push(new Floor(i, config.totalFloors));
    }

    // Initialize elevators (all start at floor 0)
    for (let i = 0; i < config.totalElevators; i++) {
      this.elevators.push(new Elevator(i, 0));
    }
  }

  requestElevator(floor: number, direction: RequestDirection): void {
    const floorObj = this.floors[floor];

    if (direction === RequestDirection.UP) {
      if (floorObj.upButtonPressed) return; // Already requested
      floorObj.pressUpButton();
    } else {
      if (floorObj.downButtonPressed) return; // Already requested
      floorObj.pressDownButton();
    }

    // Try to assign to an elevator immediately
    const elevator = this.findBestElevator(floor, direction);
    if (elevator) {
      this.assignElevatorToRequest(elevator, floor, direction);
    } else {
      // Queue the request
      this.requestQueue.push({ floor, direction });
    }
  }

  selectFloor(elevatorId: number, floor: number): void {
    const elevator = this.elevators[elevatorId];
    if (!elevator) return;

    elevator.addDestination(floor);
    this.updateElevatorButtonVisuals(elevatorId);

    if (elevator.state === ElevatorState.IDLE) {
      elevator.updateDirection();
      this.startElevatorMovement(elevator);
    }
  }

  findBestElevator(floor: number, direction: RequestDirection): Elevator | null {
    // Priority 1: Idle elevators
    const idleElevators = this.elevators.filter(e => e.state === ElevatorState.IDLE);
    if (idleElevators.length > 0) {
      return idleElevators.reduce((closest, curr) =>
        Math.abs(curr.currentFloor - floor) < Math.abs(closest.currentFloor - floor) ? curr : closest
      );
    }

    // Priority 2: Moving elevators going in the same direction and can pick up
    const suitableElevators = this.elevators.filter(e => {
      if (e.state !== ElevatorState.MOVING) return false;

      if (direction === RequestDirection.UP && e.direction === ElevatorDirection.UP) {
        return e.currentFloor <= floor;
      } else if (direction === RequestDirection.DOWN && e.direction === ElevatorDirection.DOWN) {
        return e.currentFloor >= floor;
      }
      return false;
    });

    if (suitableElevators.length > 0) {
      return suitableElevators.reduce((closest, curr) =>
        Math.abs(curr.currentFloor - floor) < Math.abs(closest.currentFloor - floor) ? curr : closest
      );
    }

    // No suitable elevator found
    return null;
  }

  assignElevatorToRequest(elevator: Elevator, floor: number, direction: RequestDirection): void {
    elevator.addDestination(floor);

    if (elevator.state === ElevatorState.IDLE) {
      elevator.updateDirection();
      this.startElevatorMovement(elevator);
    }
  }

  startElevatorMovement(elevator: Elevator): void {
    if (elevator.state === ElevatorState.MOVING) return;

    elevator.state = ElevatorState.MOVING;
    elevator.updateVisualState();
    this.moveElevator(elevator);
  }

  async moveElevator(elevator: Elevator): Promise<void> {
    while (elevator.hasDestinations()) {
      const nextFloor = elevator.getNextDestination();

      if (nextFloor === null) break;

      // Move towards the next floor
      while (elevator.currentFloor !== nextFloor) {
        await this.sleep(this.config.floorTravelTime);
        elevator.moveToNextFloor();
      }

      // Arrived at floor
      elevator.state = ElevatorState.STOPPED;
      elevator.updateVisualState();

      // Clear the destination
      elevator.removeDestination(nextFloor);
      this.updateElevatorButtonVisuals(elevator.id);

      // Clear floor button if it matches the direction
      const floor = this.floors[nextFloor];
      if (elevator.direction === ElevatorDirection.UP && floor.upButtonPressed) {
        floor.clearUpButton();
      } else if (elevator.direction === ElevatorDirection.DOWN && floor.downButtonPressed) {
        floor.clearDownButton();
      } else if (elevator.direction === ElevatorDirection.IDLE) {
        // Clear both buttons if elevator was idle
        if (floor.upButtonPressed) floor.clearUpButton();
        if (floor.downButtonPressed) floor.clearDownButton();
      }

      // Wait for doors to open/close
      await this.sleep(this.config.doorOpenTime);

      // Update direction for next move
      elevator.updateDirection();

      // Check if we should continue moving
      if (elevator.hasDestinations()) {
        elevator.state = ElevatorState.MOVING;
        elevator.updateVisualState();
      }
    }

    // No more destinations
    elevator.state = ElevatorState.IDLE;
    elevator.direction = ElevatorDirection.IDLE;
    elevator.updateVisualState();

    // Process queued requests
    this.processQueue();
  }

  processQueue(): void {
    if (this.requestQueue.length === 0) return;

    const processedIndices: number[] = [];

    for (let i = 0; i < this.requestQueue.length; i++) {
      const request = this.requestQueue[i];
      const elevator = this.findBestElevator(request.floor, request.direction);

      if (elevator) {
        this.assignElevatorToRequest(elevator, request.floor, request.direction);
        processedIndices.push(i);
      }
    }

    // Remove processed requests
    this.requestQueue = this.requestQueue.filter((_, index) => !processedIndices.includes(index));
  }

  updateElevatorButtonVisuals(elevatorId: number): void {
    const elevator = this.elevators[elevatorId];
    const panel = document.querySelector(`#elevator-${elevatorId}-panel`);

    if (panel) {
      const buttons = panel.querySelectorAll('.floor-button');
      buttons.forEach((button) => {
        const floor = parseInt(button.getAttribute('data-floor') || '0');
        if (elevator.destinationFloors.has(floor)) {
          button.classList.add('selected');
        } else {
          button.classList.remove('selected');
        }
      });
    }
  }

  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  getStatus(): string {
    let status = 'Elevator Status:\n';
    this.elevators.forEach(e => {
      status += `Elevator ${e.id}: Floor ${e.currentFloor}, ${e.direction}, ${e.state}\n`;
    });
    return status;
  }
}
"}

{"filePath": "/Users/championswimmer/Development/Personal/LLM/elevator-bench/outputs/copilot_claude-sonnet-45/script.ts", "content": "import { Building, Config } from './Building.js';
import { RequestDirection } from './Floor.js';

// Configuration
const CONFIG: Config = {
  totalFloors: 10,
  totalElevators: 4,
  floorTravelTime: 1000, // 1 second per floor
  doorOpenTime: 1500, // 1.5 seconds for doors
};

let building: Building;

function initializeBuilding(): void {
  building = new Building(CONFIG);
  renderBuilding();
  setupEventListeners();
}

function renderBuilding(): void {
  renderShaft();
  renderFloors();
  renderElevators();
  renderControlPanels();
}

function renderShaft(): void {
  const shaftContainer = document.getElementById('shaft-container');
  if (!shaftContainer) return;

  shaftContainer.innerHTML = '';
  shaftContainer.style.height = `${CONFIG.totalFloors * 80}px`;

  // Create elevator shafts
  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const shaft = document.createElement('div');
    shaft.className = 'elevator-shaft';
    shaft.id = `shaft-${i}`;
    shaftContainer.appendChild(shaft);
  }
}

function renderFloors(): void {
  const floorsContainer = document.getElementById('floors-container');
  if (!floorsContainer) return;

  floorsContainer.innerHTML = '';

  // Render floors from top to bottom (reverse order for visual display)
  for (let i = CONFIG.totalFloors - 1; i >= 0; i--) {
    const floor = building.floors[i];
    const floorDiv = document.createElement('div');
    floorDiv.className = 'floor';
    floorDiv.id = `floor-${i}`;

    const floorNumber = document.createElement('div');
    floorNumber.className = 'floor-number';
    floorNumber.textContent = `${i}`;

    const buttonsDiv = document.createElement('div');
    buttonsDiv.className = 'floor-buttons';

    if (floor.hasUpButton) {
      const upButton = document.createElement('button');
      upButton.className = 'floor-call-button up-button';
      upButton.innerHTML = '';
      upButton.onclick = () => handleFloorRequest(i, RequestDirection.UP);
      buttonsDiv.appendChild(upButton);
      floor.setUpButtonElement(upButton);
    }

    if (floor.hasDownButton) {
      const downButton = document.createElement('button');
      downButton.className = 'floor-call-button down-button';
      downButton.innerHTML = '';
      downButton.onclick = () => handleFloorRequest(i, RequestDirection.DOWN);
      buttonsDiv.appendChild(downButton);
      floor.setDownButtonElement(downButton);
    }

    floorDiv.appendChild(floorNumber);
    floorDiv.appendChild(buttonsDiv);
    floorsContainer.appendChild(floorDiv);
  }
}

function renderElevators(): void {
  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const shaft = document.getElementById(`shaft-${i}`);
    if (!shaft) continue;

    const elevator = building.elevators[i];
    const elevatorDiv = document.createElement('div');
    elevatorDiv.className = 'elevator';
    elevatorDiv.id = `elevator-${i}`;
    elevatorDiv.setAttribute('data-floor', '0');
    elevatorDiv.setAttribute('data-state', 'IDLE');
    elevatorDiv.setAttribute('data-direction', 'IDLE');

    const elevatorInfo = document.createElement('div');
    elevatorInfo.className = 'elevator-info';

    const elevatorId = document.createElement('div');
    elevatorId.className = 'elevator-id';
    elevatorId.textContent = `E${i}`;

    const elevatorFloor = document.createElement('div');
    elevatorFloor.className = 'elevator-floor';
    elevatorFloor.textContent = '0';

    const elevatorDirection = document.createElement('div');
    elevatorDirection.className = 'elevator-direction';
    elevatorDirection.textContent = '';

    elevatorInfo.appendChild(elevatorId);
    elevatorInfo.appendChild(elevatorFloor);
    elevatorInfo.appendChild(elevatorDirection);
    elevatorDiv.appendChild(elevatorInfo);

    shaft.appendChild(elevatorDiv);
    elevator.setElement(elevatorDiv);
    elevator.updateVisualPosition();
  }
}

function renderControlPanels(): void {
  const panelsContainer = document.getElementById('control-panels');
  if (!panelsContainer) return;

  panelsContainer.innerHTML = '';

  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const panel = document.createElement('div');
    panel.className = 'control-panel';
    panel.id = `elevator-${i}-panel`;

    const panelTitle = document.createElement('div');
    panelTitle.className = 'panel-title';
    panelTitle.textContent = `Elevator ${i}`;

    const buttonsGrid = document.createElement('div');
    buttonsGrid.className = 'panel-buttons';

    // Create floor buttons (from top to bottom)
    for (let floor = CONFIG.totalFloors - 1; floor >= 0; floor--) {
      const button = document.createElement('button');
      button.className = 'floor-button';
      button.textContent = floor.toString();
      button.setAttribute('data-floor', floor.toString());
      button.onclick = () => handleElevatorFloorSelection(i, floor);
      buttonsGrid.appendChild(button);
    }

    panel.appendChild(panelTitle);
    panel.appendChild(buttonsGrid);
    panelsContainer.appendChild(panel);
  }
}

function handleFloorRequest(floor: number, direction: RequestDirection): void {
  building.requestElevator(floor, direction);
}

function handleElevatorFloorSelection(elevatorId: number, floor: number): void {
  building.selectFloor(elevatorId, floor);
}

function setupEventListeners(): void {
  // Any additional global event listeners can go here
}

// Initialize when DOM is ready
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', initializeBuilding);
} else {
  initializeBuilding();
}

// Export for potential external use
export { building, CONFIG };
"}

