  padding: 30px;
  gap: 30px;
  min-height: 800px;
}

.simulation-area {
  flex: 2;
  display: flex;
  gap: 20px;
  background: #f7fafc;
  border-radius: 10px;
  padding: 20px;
  position: relative;
}

#floors-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-width: 120px;
}

.floor {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  padding: 10px;
  border-bottom: 2px solid #cbd5e0;
  background: white;
  border-radius: 5px;
  margin-bottom: 2px;
  transition: background 0.3s;
}

.floor:hover {
  background: #edf2f7;
}

.floor-number {
  font-size: 1.8em;
  font-weight: bold;
  color: #4a5568;
  min-width: 40px;
  text-align: center;
}

.floor-buttons {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.floor-call-button {
  width: 40px;
  height: 30px;
  border: 2px solid #4299e1;
  background: white;
  color: #4299e1;
  font-size: 1.2em;
  cursor: pointer;
  border-radius: 5px;
  transition: all 0.3s;
  font-weight: bold;
}

.floor-call-button:hover {
  background: #4299e1;
  color: white;
  transform: scale(1.1);
}

.floor-call-button.pressed {
  background: #f6ad55;
  border-color: #ed8936;
  color: white;
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

#shaft-container {
  display: flex;
  gap: 10px;
  position: relative;
  flex: 1;
  min-width: 400px;
}

.elevator-shaft {
  flex: 1;
  background: linear-gradient(to bottom, #e2e8f0 0%, #cbd5e0 100%);
  border: 3px solid #a0aec0;
  border-radius: 10px;
  position: relative;
  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.1);
}

.elevator {
  position: absolute;
  width: calc(100% - 10px);
  height: 70px;
  background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);
  border: 3px solid #2f855a;
  border-radius: 8px;
  left: 5px;
  bottom: 10px;
  transition: bottom 1s linear;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
}

.elevator[data-state="MOVING"] {
  background: linear-gradient(135deg, #4299e1 0%, #3182ce 100%);
  border-color: #2c5282;
}

.elevator[data-state="STOPPED"] {
  background: linear-gradient(135deg, #f6ad55 0%, #ed8936 100%);
  border-color: #dd6b20;
}

.elevator-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  color: white;
  font-weight: bold;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
}

.elevator-id {
  font-size: 0.9em;
  margin-bottom: 3px;
}

.elevator-floor {
  font-size: 1.5em;
  margin-bottom: 3px;
}

.elevator-direction {
  font-size: 1.2em;
}

.control-area {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 15px;
  min-width: 300px;
}

.control-panel {
  background: white;
  border-radius: 10px;
  padding: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid #e2e8f0;
}

.panel-title {
  font-size: 1.2em;
  font-weight: bold;
  color: #2d3748;
  margin-bottom: 10px;
  text-align: center;
  padding-bottom: 10px;
  border-bottom: 2px solid #e2e8f0;
}

.panel-buttons {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 8px;
}

.floor-button {
  width: 100%;
  aspect-ratio: 1;
  border: 2px solid #a0aec0;
  background: white;
  color: #4a5568;
  font-size: 1em;
  font-weight: bold;
  cursor: pointer;
  border-radius: 50%;
  transition: all 0.3s;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.floor-button:hover {
  background: #edf2f7;
