  totalFloors: 10,
  totalElevators: 4,
  floorTravelTime: 1000, // 1 second per floor
  doorOpenTime: 1500, // 1.5 seconds for doors
};

let building: Building;

function initializeBuilding(): void {
  building = new Building(CONFIG);
  renderBuilding();
  setupEventListeners();
}

function renderBuilding(): void {
  renderShaft();
  renderFloors();
  renderElevators();
  renderControlPanels();
}

function renderShaft(): void {
  const shaftContainer = document.getElementById('shaft-container');
  if (!shaftContainer) return;

  shaftContainer.innerHTML = '';
  shaftContainer.style.height = `${CONFIG.totalFloors * 80}px`;

  // Create elevator shafts
  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const shaft = document.createElement('div');
    shaft.className = 'elevator-shaft';
    shaft.id = `shaft-${i}`;
    shaftContainer.appendChild(shaft);
  }
}

function renderFloors(): void {
  const floorsContainer = document.getElementById('floors-container');
  if (!floorsContainer) return;

  floorsContainer.innerHTML = '';

  // Render floors from top to bottom (reverse order for visual display)
  for (let i = CONFIG.totalFloors - 1; i >= 0; i--) {
    const floor = building.floors[i];
    const floorDiv = document.createElement('div');
    floorDiv.className = 'floor';
    floorDiv.id = `floor-${i}`;

    const floorNumber = document.createElement('div');
    floorNumber.className = 'floor-number';
    floorNumber.textContent = `${i}`;

    const buttonsDiv = document.createElement('div');
    buttonsDiv.className = 'floor-buttons';

    if (floor.hasUpButton) {
      const upButton = document.createElement('button');
      upButton.className = 'floor-call-button up-button';
      upButton.innerHTML = '';
      upButton.onclick = () => handleFloorRequest(i, RequestDirection.UP);
      buttonsDiv.appendChild(upButton);
      floor.setUpButtonElement(upButton);
    }

    if (floor.hasDownButton) {
      const downButton = document.createElement('button');
      downButton.className = 'floor-call-button down-button';
      downButton.innerHTML = '';
      downButton.onclick = () => handleFloorRequest(i, RequestDirection.DOWN);
      buttonsDiv.appendChild(downButton);
      floor.setDownButtonElement(downButton);
    }

    floorDiv.appendChild(floorNumber);
    floorDiv.appendChild(buttonsDiv);
    floorsContainer.appendChild(floorDiv);
  }
}

function renderElevators(): void {
  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const shaft = document.getElementById(`shaft-${i}`);
    if (!shaft) continue;

    const elevator = building.elevators[i];
    const elevatorDiv = document.createElement('div');
    elevatorDiv.className = 'elevator';
    elevatorDiv.id = `elevator-${i}`;
    elevatorDiv.setAttribute('data-floor', '0');
    elevatorDiv.setAttribute('data-state', 'IDLE');
    elevatorDiv.setAttribute('data-direction', 'IDLE');

    const elevatorInfo = document.createElement('div');
    elevatorInfo.className = 'elevator-info';

    const elevatorId = document.createElement('div');
    elevatorId.className = 'elevator-id';
    elevatorId.textContent = `E${i}`;

    const elevatorFloor = document.createElement('div');
    elevatorFloor.className = 'elevator-floor';
    elevatorFloor.textContent = '0';

    const elevatorDirection = document.createElement('div');
    elevatorDirection.className = 'elevator-direction';
    elevatorDirection.textContent = '';

    elevatorInfo.appendChild(elevatorId);
    elevatorInfo.appendChild(elevatorFloor);
    elevatorInfo.appendChild(elevatorDirection);
    elevatorDiv.appendChild(elevatorInfo);

    shaft.appendChild(elevatorDiv);
    elevator.setElement(elevatorDiv);
    elevator.updateVisualPosition();
  }
}

function renderControlPanels(): void {
  const panelsContainer = document.getElementById('control-panels');
  if (!panelsContainer) return;

  panelsContainer.innerHTML = '';

  for (let i = 0; i < CONFIG.totalElevators; i++) {
    const panel = document.createElement('div');
    panel.className = 'control-panel';
    panel.id = `elevator-${i}-panel`;

    const panelTitle = document.createElement('div');
    panelTitle.className = 'panel-title';
    panelTitle.textContent = `Elevator ${i}`;

    const buttonsGrid = document.createElement('div');
    buttonsGrid.className = 'panel-buttons';

    // Create floor buttons (from top to bottom)
    for (let floor = CONFIG.totalFloors - 1; floor >= 0; floor--) {
      const button = document.createElement('button');
      button.className = 'floor-button';
      button.textContent = floor.toString();
      button.setAttribute('data-floor', floor.toString());
      button.onclick = () => handleElevatorFloorSelection(i, floor);
      buttonsGrid.appendChild(button);
    }

    panel.appendChild(panelTitle);
    panel.appendChild(buttonsGrid);
    panelsContainer.appendChild(panel);
  }
}

function handleFloorRequest(floor: number, direction: RequestDirection): void {
  building.requestElevator(floor, direction);
}

function handleElevatorFloorSelection(elevatorId: number, floor: number): void {
  building.selectFloor(elevatorId, floor);
}

function setupEventListeners(): void {
  // Any additional global event listeners can go here
}

// Initialize when DOM is ready
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', initializeBuilding);
} else {
  initializeBuilding();
}

// Export for potential external use
export { building, CONFIG };
"}

{"content": "* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  min-height: 100vh;
  padding: 20px;
  color: #333;
}

.container {
  max-width: 1400px;
  margin: 0 auto;
  background: white;
  border-radius: 15px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  overflow: hidden;
}

header {
  background: linear-gradient(135deg, #4a5568 0%, #2d3748 100%);
  color: white;
  padding: 30px;
  text-align: center;
}

h1 {
  font-size: 2.5em;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.info-box {
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  padding: 15px;
  margin-top: 15px;
  text-align: left;
  font-size: 0.9em;
  backdrop-filter: blur(10px);
}

.info-box h3 {
  margin-bottom: 10px;
  color: #ffd700;
}

.info-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
  flex-wrap: wrap;
}

.info-item {
  margin-right: 20px;
  margin-bottom: 5px;
}

.info-label {
  font-weight: bold;
  color: #a0aec0;
}

.info-value {
  color: #e2e8f0;
}

main {
  display: flex;
  padding: 30px;
  gap: 30px;
  min-height: 800px;
}

.simulation-area {
  flex: 2;
  display: flex;
  gap: 20px;
  background: #f7fafc;
  border-radius: 10px;
  padding: 20px;
  position: relative;
}

#floors-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-width: 120px;
}

.floor {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  padding: 10px;
  border-bottom: 2px solid #cbd5e0;
  background: white;
  border-radius: 5px;
  margin-bottom: 2px;
  transition: background 0.3s;
}

.floor:hover {
  background: #edf2f7;
}

.floor-number {
  font-size: 1.8em;
  font-weight: bold;
  color: #4a5568;
  min-width: 40px;
  text-align: center;
}

.floor-buttons {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.floor-call-button {
  width: 40px;
  height: 30px;
  border: 2px solid #4299e1;
  background: white;
  color: #4299e1;
  font-size: 1.2em;
  cursor: pointer;
  border-radius: 5px;
  transition: all 0.3s;
  font-weight: bold;
}

.floor-call-button:hover {
  background: #4299e1;
  color: white;
  transform: scale(1.1);
}

.floor-call-button.pressed {
  background: #f6ad55;
  border-color: #ed8936;
  color: white;
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

#shaft-container {
  display: flex;
  gap: 10px;
  position: relative;
  flex: 1;
  min-width: 400px;
}

.elevator-shaft {
  flex: 1;
  background: linear-gradient(to bottom, #e2e8f0 0%, #cbd5e0 100%);
  border: 3px solid #a0aec0;
  border-radius: 10px;
  position: relative;
  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.1);
}

.elevator {
  position: absolute;
  width: calc(100% - 10px);
  height: 70px;
  background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);
  border: 3px solid #2f855a;
  border-radius: 8px;
  left: 5px;
  bottom: 10px;
  transition: bottom 1s linear;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
}

.elevator[data-state="MOVING"] {
  background: linear-gradient(135deg, #4299e1 0%, #3182ce 100%);
  border-color: #2c5282;
}

.elevator[data-state="STOPPED"] {
  background: linear-gradient(135deg, #f6ad55 0%, #ed8936 100%);
  border-color: #dd6b20;
}

.elevator-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  color: white;
  font-weight: bold;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
}

.elevator-id {
  font-size: 0.9em;
  margin-bottom: 3px;
}

.elevator-floor {
  font-size: 1.5em;
  margin-bottom: 3px;
}

.elevator-direction {
  font-size: 1.2em;
}

.control-area {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 15px;
  min-width: 300px;
}

.control-panel {
  background: white;
  border-radius: 10px;
  padding: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid #e2e8f0;
}

.panel-title {
  font-size: 1.2em;
  font-weight: bold;
  color: #2d3748;
  margin-bottom: 10px;
  text-align: center;
  padding-bottom: 10px;
  border-bottom: 2px solid #e2e8f0;
}

.panel-buttons {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 8px;
}

.floor-button {
  width: 100%;
  aspect-ratio: 1;
  border: 2px solid #a0aec0;
  background: white;
  color: #4a5568;
  font-size: 1em;
  font-weight: bold;
  cursor: pointer;
  border-radius: 50%;
  transition: all 0.3s;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.floor-button:hover {
  background: #edf2f7;
  transform: scale(1.1);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.floor-button.selected {
  background: linear-gradient(135deg, #f6ad55 0%, #ed8936 100%);
  border-color: #dd6b20;
  color: white;
  animation: glow 1s infinite alternate;
}

@keyframes glow {
  from {
    box-shadow: 0 0 5px #ed8936, 0 0 10px #ed8936;
  }
  to {
    box-shadow: 0 0 10px #f6ad55, 0 0 20px #f6ad55;
  }
}

footer {
  background: #2d3748;
  color: #a0aec0;
  padding: 20px;
  text-align: center;
  font-size: 0.9em;
}

@media (max-width: 1200px) {
  main {
    flex-direction: column;
  }

  .control-area {
    flex-direction: row;
    flex-wrap: wrap;
  }

  .control-panel {
    flex: 1;
    min-width: 250px;
  }
}

@media (max-width: 768px) {
  h1 {
    font-size: 1.8em;
  }

  .simulation-area {
    flex-direction: column;
  }

  #shaft-container {
    min-width: 100%;
    height: 400px;
  }

  .panel-buttons {
    grid-template-columns: repeat(5, 1fr);
  }
}
