  clearDownButton(): void {
    this.downButtonPressed = false;
    this.updateButtonVisuals();
  }

  updateButtonVisuals(): void {
    if (this.upButtonElement) {
      if (this.upButtonPressed) {
        this.upButtonElement.classList.add('pressed');
