  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  min-height: 100vh;
  padding: 20px;
  color: #333;
}

.container {
  max-width: 1400px;
  margin: 0 auto;
  background: white;
  border-radius: 15px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  overflow: hidden;
}

header {
  background: linear-gradient(135deg, #4a5568 0%, #2d3748 100%);
  color: white;
  padding: 30px;
  text-align: center;
}

h1 {
  font-size: 2.5em;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.info-box {
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  padding: 15px;
  margin-top: 15px;
  text-align: left;
  font-size: 0.9em;
  backdrop-filter: blur(10px);
}

.info-box h3 {
  margin-bottom: 10px;
  color: #ffd700;
}

.info-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
  flex-wrap: wrap;
}

.info-item {
  margin-right: 20px;
  margin-bottom: 5px;
}

.info-label {
  font-weight: bold;
  color: #a0aec0;
}

.info-value {
  color: #e2e8f0;
}

main {
  display: flex;
  padding: 30px;
  gap: 30px;
  min-height: 800px;
}

.simulation-area {
  flex: 2;
  display: flex;
  gap: 20px;
  background: #f7fafc;
  border-radius: 10px;
  padding: 20px;
  position: relative;
}

#floors-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-width: 120px;
}

.floor {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  padding: 10px;
  border-bottom: 2px solid #cbd5e0;
  background: white;
  border-radius: 5px;
  margin-bottom: 2px;
  transition: background 0.3s;
}

.floor:hover {
  background: #edf2f7;
}

.floor-number {
  font-size: 1.8em;
  font-weight: bold;
  color: #4a5568;
  min-width: 40px;
  text-align: center;
}

.floor-buttons {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.floor-call-button {
  width: 40px;
  height: 30px;
  border: 2px solid #4299e1;
  background: white;
  color: #4299e1;
  font-size: 1.2em;
  cursor: pointer;
  border-radius: 5px;
  transition: all 0.3s;
  font-weight: bold;
}

.floor-call-button:hover {
  background: #4299e1;
  color: white;
  transform: scale(1.1);
}

.floor-call-button.pressed {
  background: #f6ad55;
  border-color: #ed8936;
  color: white;
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

#shaft-container {
  display: flex;
  gap: 10px;
  position: relative;
  flex: 1;
  min-width: 400px;
}

.elevator-shaft {
  flex: 1;
  background: linear-gradient(to bottom, #e2e8f0 0%, #cbd5e0 100%);
  border: 3px solid #a0aec0;
  border-radius: 10px;
  position: relative;
  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.1);
