.elevator {
  position: absolute;
  width: calc(100% - 10px);
  height: 70px;
  background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);
  border: 3px solid #2f855a;
  border-radius: 8px;
  left: 5px;
  bottom: 10px;
  transition: bottom 1s linear;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
