.elevator[data-state="MOVING"] {
  background: linear-gradient(135deg, #4299e1 0%, #3182ce 100%);
  border-color: #2c5282;
}

.elevator[data-state="STOPPED"] {
  background: linear-gradient(135deg, #f6ad55 0%, #ed8936 100%);
  border-color: #dd6b20;
}

.elevator-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  color: white;
  font-weight: bold;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
}

.elevator-id {
  font-size: 0.9em;
  margin-bottom: 3px;
}

.elevator-floor {
  font-size: 1.5em;
  margin-bottom: 3px;
}

.elevator-direction {
  font-size: 1.2em;
}

.control-area {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 15px;
  min-width: 300px;
}

.control-panel {
  background: white;
  border-radius: 10px;
  padding: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid #e2e8f0;
}

.panel-title {
  font-size: 1.2em;
  font-weight: bold;
  color: #2d3748;
  margin-bottom: 10px;
  text-align: center;
  padding-bottom: 10px;
  border-bottom: 2px solid #e2e8f0;
}

.panel-buttons {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 8px;
}

.floor-button {
  width: 100%;
  aspect-ratio: 1;
  border: 2px solid #a0aec0;
  background: white;
  color: #4a5568;
  font-size: 1em;
  font-weight: bold;
  cursor: pointer;
  border-radius: 50%;
  transition: all 0.3s;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.floor-button:hover {
  background: #edf2f7;
  transform: scale(1.1);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.floor-button.selected {
  background: linear-gradient(135deg, #f6ad55 0%, #ed8936 100%);
  border-color: #dd6b20;
  color: white;
  animation: glow 1s infinite alternate;
}

@keyframes glow {
  from {
    box-shadow: 0 0 5px #ed8936, 0 0 10px #ed8936;
  }
  to {
    box-shadow: 0 0 10px #f6ad55, 0 0 20px #f6ad55;
  }
}

footer {
  background: #2d3748;
  color: #a0aec0;
  padding: 20px;
  text-align: center;
  font-size: 0.9em;
}

@media (max-width: 1200px) {
  main {
    flex-direction: column;
  }

  .control-area {
    flex-direction: row;
    flex-wrap: wrap;
  }

  .control-panel {
    flex: 1;
    min-width: 250px;
  }
}

@media (max-width: 768px) {
  h1 {
    font-size: 1.8em;
  }

  .simulation-area {
    flex-direction: column;
  }

  #shaft-container {
    min-width: 100%;
    height: 400px;
  }

  .panel-buttons {
    grid-template-columns: repeat(5, 1fr);
  }
}
