                        minDistance = distance;
                        bestElevator = elevator;
                    }
                } else if (direction === Direction.DOWN && elevator.currentFloor >= floor) {
                    if (distance < minDistance) {
                        minDistance = distance;
                        bestElevator = elevator;
                    }
                }
            }
        }

        return bestElevator;
    }

