{"content":"export enum Direction {
    UP = 'up',
    DOWN = 'down',
    IDLE = 'idle'
}

export enum ElevatorState {
    IDLE = 'idle',
    MOVING = 'moving',
    LOADING = 'loading'
}

export interface FloorRequest {
    floor: number;
    direction: Direction;
}

export const CONFIG = {
